
public class BreakEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		do{
			System.out.println("Anurag");
			if(a==3) {
				break;
			}
			a++;
			System.out.println("Kumar");
		}while(a<=5);
		System.out.println("Bye");
		
	}

}
