
public class ContinueEx4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		while(a<=5) {
			System.out.println("Anurag");
			if(a==3) {
				continue;
			}
			a++;
			System.out.println("Kumar");
		}
		System.out.println("Bye");
		
	}

}
